import Todo from "./components/todo/Todo";
import AddTodo from "./components/todo/AddTodo";
import {
  TodoContext,
  TodoContextProvider,
} from "./components/todo/contexts/TodoContext";
import { useContext } from "react";

export default function App() {
  // state와 관련된 모든 것들 삭제
  // 근데 아래에서 state가 필요함 -> contextTodo()

  // context를 땡겨오려고 함.
  const { contextTodo } = useContext(TodoContext);

  return (
    <TodoContextProvider>
      <div>
        <h4>
          완료: {todoList.filter((todo) => todo.isDone).length} / 미완료:{" "}
          {todoList.filter((todo) => !todo.isDone).length}
        </h4>
        <ul>
          {todoList.map((todo) => (
            <Todo
              key={todo.id}
              todo={todo}
              onClickDoneHandler={onDoneHandler}
            />
          ))}
        </ul>
        {/* 일정추가 */}
        <AddTodo setTodoList={setTodoList} />
      </div>
    </TodoContextProvider>
  );
}
